import React, {useState,useEffect,Component} from 'react';
import { 
  View,
  Text,
  TextInput,
  StyleSheet,
  ScrollView,
  StatusBar,
  SafeAreaView,
  Touchable,
  Image,
  TouchableOpacity,
  Shape,
  ImageBackground,
  Modal,
  Linking,
  Pressable
 } from 'react-native';
 import axios from "axios"
 import DrawerActions from "react-navigation"
 import Icon from "react-native-vector-icons/Ionicons"
 import LinearGradient from 'react-native-linear-gradient';
 import getDirections from 'react-native-google-maps-directions'
 import openMap from 'react-native-open-maps';
 import createMapLink from 'react-native-open-maps';
 export default class iücdoluluk extends Component{
 

  constructor(props)
  {

    super(props)
    this.state={
 doluluk:0,
 bosyer:0,
 modalVisible3:false,
 
 carPark2:{},
 show:false
    }
}
functionTwo(){
 console.log('navbar')
} 
_goToYosemite() {
  const {longitude2,latitude2} = this.props.route.params
  openMap({ latitude:latitude2, longitude:longitude2});
  createMapLink({ provider: 'google', end: 'Los Altos Taqueria' })
}
setModalVisible3 = (visible) => {
  this.setState({ modalVisible3: visible });
}

handleGetDirections = () => {
  const data = {
     source: {
      latitude: 41.99449778084676,
      longitude: 29.728089555825388
    },
    destination: {
      latitude: 41.98449778084676,
      longitude:29.788089555825388
    },
    params: [
      {
        key: "travelmode",
        value: "driving"        // may be "walking", "bicycling" or "transit" as well
      },
      {
        key: "dir_action",
        value: "navigate"       // this instantly initializes navigation using the given travel mode
      }
    ],

  }

  getDirections(data)
}

  componentDidMount() {
   
  }
  render(){
    
 const {carPark2,modalVisible3}=this.state


return(
     
       <View style={{backgroundColor:'#80cbc4',width:'100%',height:'100%'}}>
<ImageBackground source={require('../../../../assets/Anitkabir.jpg')} resizeMode="cover" style={styles.image}>
     <View style={{alignItems:'center'}}> 
   <Text style={{color:'white',fontSize:25,alignItems:'center',justifyContent:'center'}}>
   Anıtkabir
     </Text>
     </View>
<TouchableOpacity onPress={()=>this.props.navigation.navigate('mapAnk')} style={{position:'absolute',right:'85%',top:'10%'}}>
    <Icon  name={Platform.OS === "ios" ? "ios-add" : "arrow-back-circle-outline"}
  //name={(this.state.hidePassword)?"eye-off-outlane:eye-outlane"}  şifre görünürlüğü açıp kapatma
  color="white"
  size={55}/>
</TouchableOpacity>
<TouchableOpacity onPress={()=> this.props.navigation.openDrawer(this.functionTwo()) } style={{position:'absolute',right:'85%',top:'0%'}}>
    <Icon  name={Platform.OS === "ios" ? "ios-add" : "menu-outline"}
  color="white"
  size={55}/>
</TouchableOpacity>
<View style={{alignItems: 'center',marginTop:35}}>




  </View>


        
        {
        //GOOGLE MAPS DİRECTİON
        }
      
        <View style={{position: 'absolute', left: 0, right: 0, bottom: 600, flexDirection:'row-reverse',marginBottom:'0%',marginRight:'3%'}}>
      <TouchableOpacity style={{alignItems: "center",
      
    backgroundColor: "#adf6d6",
    padding: 10, borderRadius:25}} onPress={()=>this.setModalVisible3(true)}>
      <Icon  name={Platform.OS === "ios" ? "ios-add" : "compass-outline"}
  color="black"
  size={45}>
        </Icon>
        
        </TouchableOpacity></View>
      
      
      
        <View style={{position: 'absolute', left: 0, right: 0, bottom: 400, flexDirection:'row-reverse',marginBottom:'0%',marginRight:'3%'}}>
      <TouchableOpacity style={{alignItems: "center",
      
    backgroundColor: "#adf6d6",
    padding: 10, borderRadius:55}} onPress={()=>Linking.openURL('https://www.google.com/search?q=an%C4%B1tkabir&rlz=1C1SQJL_trTR938TR938&sxsrf=AOaemvKnkwco5Os2cTDZ5xiN99pRihdXIA:1633087469552&source=lnms&tbm=isch&sa=X&ved=2ahUKEwjYs4n0jKnzAhVDy6QKHQi1B6kQ_AUoAnoECAEQBA&biw=1920&bih=880&dpr=1')}>
      <Icon  name={Platform.OS === "ios" ? "ios-add" : "image-outline"}
  color="black"
  size={45}>
        </Icon>
        
        </TouchableOpacity></View>






        <View style={{position: 'absolute', left: 0, right: 0, bottom: 200, flexDirection:'row-reverse',marginBottom:'0%',marginRight:'3%'}}>
      <TouchableOpacity style={{alignItems: "center",
      
    backgroundColor: "#adf6d6",
    padding: 10, borderRadius:55}} onPress={()=>this.props.navigation.navigate('AnitkabirInfo')}>
      <Icon  name={Platform.OS === "ios" ? "ios-add" : "information"}
  color="black"
  size={45}>
        </Icon>
        
        </TouchableOpacity></View>



        <Modal
          animationType="slide"
          transparent={true}
          visible={modalVisible3}
          onRequestClose={() => {
            Alert.alert("Modal has been closed.");
            this.setModalVisible3(!modalVisible3);
          }}
        >
          <View style={styles.centeredView}>
            <View style={styles.modalView}>
              <Text style={styles.modalText}>Yol tarifine gitmek istiyor musunuz?</Text>
              <Pressable
                style={[styles.button3, styles.buttonClose1]}
                onPress={() => this.setModalVisible3(!modalVisible3)}
              >
                <Text style={styles.textStyle2}>Hayır</Text>
              </Pressable>
              <Pressable
                style={[styles.button3, styles.buttonClose2]}
                onPress={() =>{this._goToYosemite(),this.setModalVisible3(!modalVisible3)}}
              >
                <Text style={styles.textStyle2}>Evet</Text>
              </Pressable>
            </View>
          </View>
        </Modal>
    
        </ImageBackground>

        </View>
       )}
 }
 const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  image: {
    flex: 1,
    
  },
  //MODAL STYLES
  modalView: {
    
    backgroundColor: "#687e9b",
    borderRadius: 20,
    padding: 15,
    alignItems: "center",
    shadowColor: "blue",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5
  },
  modalText: {
    marginBottom: 15,
    fontSize:20,
    textAlign: "center",
    color:'white',fontWeight:'bold'
  },
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22,
    marginBottom:25
  },
  button3: {
    borderRadius: 20,
    padding: 10,
    marginTop:20,
    width:80
  },
  buttonOpen: {
    backgroundColor: "#008ba3",
  },
  buttonClose2: {
    backgroundColor: "green",
  },
  buttonClose1: {
    backgroundColor: "red",
  },
  textStyle2: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center"
  },
})
