import React, {useState,useEffect,Component} from 'react';
import { 
  View,
  Text,
  TextInput,
  StyleSheet,
  ScrollView,
  StatusBar,
  SafeAreaView,
  Touchable,
  Image,
  TouchableOpacity,FlatList,
  Shape,
  ImageBackground,
  Modal,
  Linking,
  Pressable
 } from 'react-native';
 import axios from "axios"
 import DrawerActions from "react-navigation"
 import Icon from "react-native-vector-icons/Ionicons"
 import LinearGradient from 'react-native-linear-gradient';
 import getDirections from 'react-native-google-maps-directions'
 import openMap from 'react-native-open-maps';
 import createMapLink from 'react-native-open-maps';
 export default class iücdoluluk extends Component{
 

  constructor(props)
  {

    super(props)
    this.state={
 
    }
}



  componentDidMount() {
   
  }
  render(){
    
 const {carPark2,modalVisible3}=this.state
const more='Other Photos..'
 const DATA = [
 
  {
    name:'Ayasofya Camii',
    photo:'https://arkeofili.com/wp-content/uploads/2020/07/ayasofya1.jpg',
    more:'Ayasofya'
  },
  {
   name:'Galata Kulesi',
    photo:'https://upload.wikimedia.org/wikipedia/commons/thumb/f/f7/Istanbul_asv2020-02_img48_Galata_Tower.jpg/1200px-Istanbul_asv2020-02_img48_Galata_Tower.jpg',
    more:'galataTower'
  },
 
  {
    name:'Sultanahmet Camii',
    photo:'https://www.carwiz.rent/data/public/2019-07/blue_mosque_15db837b846c60.jpg',
    more:'Sultanahmet'
  },
  {
    name:'Topkapı Sarayı',
    photo:'https://www.fotometrik360.com/wp-content/uploads/2019/08/Topkap%C4%B1-Saray%C4%B1-360-sanal-tur.jpg',
    more:'Topkapi'
  },
  
  
];

const Item = ({ photo,more,name }) => (
  <View>


    <TouchableOpacity style={styles.item2} onPress={()=>this.props.navigation.navigate(more)}>

    
   
     <Image
        style={{width:65, height:65,borderRadius:15}}
        source={{
          uri: photo
        }}
      />
        <Text style={{marginLeft:15,fontSize:25,color:'grey',textAlign: 'center'}}>{name}</Text>

      </TouchableOpacity>
      
  </View>
);
const renderItem = ({ item }) => (
  <Item photo={item.photo} more={item.more} name={item.name} />
);

return(
     
       <View style={{backgroundColor:'black',width:'100%',height:'100%'}}>
     <View style={{alignItems: 'center',justifyContent: 'center',marginTop:20}}>
          <Text style={({marginTop:-10,color: '#283b50', fontSize: 45})}>
          İSTANBUL
          </Text>
          </View>
<View style={{alignItems: 'center',marginTop:35}}>


<FlatList
        data={DATA}
        renderItem={renderItem}
        
      />

  </View>


        
        
      
      




       


    

        </View>
       )}
 }
 const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  image: {
    flex: 1,
    
  },
  item2: {
    borderRadius:15,
   marginLeft:15,
    flexDirection: 'row',
   marginTop:55,
    padding: 8,
    alignItems:'center',
    marginHorizontal: 20,
  },
  tinyLogo: {
    width: 10,
    height: 10,
  },
  
})
