import React, {useState,useEffect,Component} from 'react';
import { 
  View,
  Text,
  TextInput,
  StyleSheet,
  ScrollView,
  StatusBar,
  SafeAreaView,
  Touchable,
  Image,
  TouchableOpacity,FlatList,
  Shape,
  ImageBackground,
  Modal,
  Linking,
  Pressable
 } from 'react-native';
 import axios from "axios"
 import DrawerActions from "react-navigation"
 import Icon from "react-native-vector-icons/Ionicons"
 import LinearGradient from 'react-native-linear-gradient';
 import getDirections from 'react-native-google-maps-directions'
 import openMap from 'react-native-open-maps';
 import createMapLink from 'react-native-open-maps';
 export default class iücdoluluk extends Component{
 

  constructor(props)
  {

    super(props)
    this.state={
 doluluk:0,
 bosyer:0,
 modalVisible3:false,
 
 carPark2:{},
 show:false
    }
}
functionTwo(){
 console.log('navbar')
} 
_goToYosemite() {
  const {longitude2,latitude2} = this.props.route.params
  openMap({ latitude:latitude2, longitude:longitude2});
  createMapLink({ provider: 'google', end: 'Los Altos Taqueria' })
}
setModalVisible3 = (visible) => {
  this.setState({ modalVisible3: visible });
}

handleGetDirections = () => {
  const data = {
     source: {
      latitude: 41.99449778084676,
      longitude: 29.728089555825388
    },
    destination: {
      latitude: 41.98449778084676,
      longitude:29.788089555825388
    },
    params: [
      {
        key: "travelmode",
        value: "driving"        // may be "walking", "bicycling" or "transit" as well
      },
      {
        key: "dir_action",
        value: "navigate"       // this instantly initializes navigation using the given travel mode
      }
    ],

  }

  getDirections(data)
}

  componentDidMount() {
   
  }
  render(){
    
 const {carPark2,modalVisible3}=this.state
const more='Other Photos..'
 const DATA = [
  {
    
    photo: 'https://www.fotometrik360.com/wp-content/uploads/2019/08/Topkap%C4%B1-Saray%C4%B1-360-sanal-tur.jpg',
    more:''
  },
  {
    
    photo: 'https://blog.obilet.com/wp-content/uploads/2018/05/Topkap%C4%B1-Saray%C4%B1-1200x800.jpg',
    more:''
  },
  {
   
    photo: 'https://www.yeniasya.com.tr/Sites/YeniAsya/Upload/images/Content/2021/09/18/tkap.jpg',
    more:''
  },
 
  {
    
    photo: 'https://www.tercihiniyap.net/images/haberler/2019/11/topkapi-sarayi-kacta-acilir-kacta-kapanir-calisma-saatleri-nasil_4c05d.png',
    more:''
  },
  {
    
    photo: 'https://im.haberturk.com/2021/09/03/3180783_fdf054719631b25ec64eab2e11cd225f_640x640.jpg',
    more:''
  },
  {
    
    photo: 'https://www.google.com/search?q=topkap%C4%B1+saray%C4%B1&tbm=isch&ved=2ahUKEwj3_qDfu7PzAhXRuKQKHcd2BNYQ2-cCegQIABAA&oq=topkap%C4%B1+saray%C4%B1&gs_lcp=CgNpbWcQDFAAWABgL2gAcAB4AIABAIgBAJIBAJgBAKoBC2d3cy13aXotaW1n&sclient=img&ei=N19cYbeHLdHxkgXH7ZGwDQ&bih=903&biw=984&hl=tr',
 more:more
  },
  
];

const Item = ({ photo,more }) => (
  <View style={styles.item2}>

  <TouchableOpacity  style={{alignSelf: 'center',justifyContent: 'center'}}onPress={()=>Linking.openURL(photo)}>
<Text style={{margin:'auto',fontSize:25,color:'grey',textAlign: 'center'}}>{more}</Text>
</TouchableOpacity>

    <TouchableOpacity  onPress={()=>Linking.openURL(photo)}>

    
         
     <Image
        style={{width:55, height:55,borderRadius:15}}
        source={{
          uri: photo
        }}
      />
      </TouchableOpacity>
  </View>
);
const renderItem = ({ item }) => (
  <Item photo={item.photo} more={item.more} />
);

return(
     
       <View style={{backgroundColor:'#1e1e01',width:'100%',height:'100%'}}>
<ImageBackground source={require('../../../../assets/Topkapi2.jpg')} resizeMode="cover" style={styles.image}>
     <View style={{alignItems:'center'}}> 
   <Text style={{color:'#40581d',fontSize:30,alignItems:'center',justifyContent:'center'}}>
     Topkapi Palace
     </Text>
     </View>
<TouchableOpacity onPress={()=>this.props.navigation.navigate('mapIst')} style={{position:'absolute',right:0,top:0}}>
    <Icon  name={Platform.OS === "ios" ? "ios-add" : "arrow-back-circle-outline"}
  //name={(this.state.hidePassword)?"eye-off-outlane:eye-outlane"}  şifre görünürlüğü açıp kapatma
  color="#40581d"
  size={55}/>
</TouchableOpacity>
<TouchableOpacity onPress={()=> this.props.navigation.openDrawer(this.functionTwo()) } style={{position:'absolute',right:'85%',top:'0%'}}>
    <Icon  name={Platform.OS === "ios" ? "ios-add" : "menu-outline"}
  color="#40581d"
  size={55}/>
</TouchableOpacity>
<View style={{alignItems: 'center',marginTop:35}}>


<FlatList
        data={DATA}
        renderItem={renderItem}
        horizontal={true}
      />

  </View>


        
        {
        //GOOGLE MAPS DİRECTİON
        }
      
        <View style={{position: 'absolute', left: 0, right: 0, bottom: 600, flexDirection:'row-reverse',marginBottom:50,marginRight:35}}>
      <TouchableOpacity style={{alignItems: "center",
      
    backgroundColor: "#adf6d6",
    padding: 10, borderRadius:25}} onPress={()=>this.setModalVisible3(true)}>
      <Icon  name={Platform.OS === "ios" ? "ios-add" : "compass-outline"}
  color="black"
  size={45}>
        </Icon>
        
