import CountDown from 'react-native-countdown-component';
import CountDownTimer from 'react-native-countdown-timer-hooks';
import React,{Component,useEffect,useState
  }from 'react';
import axios from "axios"
import {View,  BackHandler,BackAndroid,ToastAndroid,StyleSheet,TextInput,Pressable, Text,Modal, TouchableOpacity,ScrollView, Image,Alert} from 'react-native';
import {Linking} from 'react-native'
import getDirections from 'react-native-google-maps-directions'
import Icon from 'react-native-vector-icons/Ionicons';
import LinearGradient from 'react-native-linear-gradient';
var profile;
let kalanSure = new Number();
let otoparkAlani;
let count;
export default class timer extends Component{
  constructor(props){
    

    super(props);
   this.state = {
      modalVisible: false,
      modalVisible2:false,
      modalVisible3:false,
      geldimi:'',
      sure:''
    };
  
  }

//GERİ GİTMEYİ ENGELLER
componentWillMount(){
  BackHandler.addEventListener('hardwareBackPress', function() {return true})
  
 

}

  onBackPress = () => {
    return true;
  }
 
  handleGetDirections = () => {
    const data = {
       source: {
        latitude: 40.99449778084676,
        longitude: 28.728089555825388
      },
      destination: {
        latitude: 40.98449778084676,
        longitude:28.788089555825388
      },
      params: [
        {
          key: "travelmode",
          value: "driving"        // may be "walking", "bicycling" or "transit" as well
        },
        {
          key: "dir_action",
          value: "navigate"       // this instantly initializes navigation using the given travel mode
        }
      ],

    }
 
    getDirections(data)
  }
  componentDidMount() {

    const {token} = this.props.route.params;
    // console.log(token);
    axios.get(`https://ieeevale.com/api/current_user`,{
      headers:{
        'authorization':token
      }
      }).then(res => {profile=res.data.data 
        axios.get(`https://ieeevale.com/api/carparks/${profile.reservation.carParkId}`,{
      headers:{
        'authorization':token
      }
      }).then(res => {
        // console.log('areas nın lengthi' + res.data.data.areas.length);
        for(let i = 0; i<res.data.data.areas.length;i++){
          if(res.data.data.areas[i].areaName == profile.reservation.reservationArea){
          kalanSure = res.data.data.areas[i].remainingTime;
          kalanSure = kalanSure*60;
          otoparkAlani = res.data.data.areas[i];
          }
        }
      })}).catch((err) => {alert(err)})
      console.log(kalanSure)
      var kalanSureString = kalanSure.toString();
      this.setState({sure:kalanSureString})
      // const sifirlandiMi = () => {
      //   if(kalanSure == 0){
      //     axios.get(`https://ieeevale.com/api/current_user`,{
      //       headers:{
      //         'authorization':token
      //       }
      //       }).then(res => {profile=res.data.data
              
      //     this.props.navigation.navigate('map',{profile,token})
      //   })
      //     .catch((err) => {
      //       alert(err);
      //     })
      //   }
      // }
      // setInterval(() => {
      //   kalanSure--;
      //    kalanSureString = ''+kalanSure;
      //   this.setState({sure:kalanSureString})
      //   // console.log(kalanSure);
      //   // sifirlandiMi();
      // }, 1000);
      
   
    axios.get(`https://api.stackexchange.com/2.2/users?page=4&order=desc&sort=reputation&site=stackoverflow`)
    .then(res => {
    this.setState({geldimi:false/*res.data.items[12].is_employee*/})
    })
    .catch(e => {console.log(e)});
    
  }
  setModalVisible = (visible) => {
    this.setState({ modalVisible: visible });
  }
  setModalVisible2 = (visible) => {
    this.setState({ modalVisible2: visible });
  }
  setModalVisible3 = (visible) => {
    this.setState({ modalVisible3: visible });
  }
 




render() {
  
  const timerCallbackFunc = () => {
   alert('Süre Doldu! Rezervasyon otomatik olarak iptal edildi.')
 
    axios.get(`https://ieeevale.com/api/current_user`,{
      headers:{
        'authorization':token
      }
      }).then(res => {profile=res.data.data
              otoparkAlani.reservationState=false,otoparkAlani.remainingTime=0
              console.log(profile);
              axios.put(`https://ieeevale.com/api/carparks/${profile.reservation.carParkId}`,otoparkAlani,{
              headers:{
                'authorization':token
              }
            }).then(res => {
              this.props.navigation.navigate('map',{profile,token}),  alert('Süre Doldu! Rezervasyon otomatik olarak iptal edildi.')
          }).catch((err) => {
            console.log(err.message);
              alert(err)
          })})

  };
  const phonnumb='05523361923'
  const { modalVisible,modalVisible2,geldimi,modalVisible3,sure } = this.state;
  const {token} = this.props.route.params;
  const refTimer=2
  kalanSure=120
console.log(kalanSure)
    return (

<LinearGradient colors={['black', 'black']} style={{flex:1}}>
<TouchableOpacity onPress={()=>this.props.navigation.openDrawer()} style={{position:'absolute',right:'85%',top:'0%'}}>
    <Icon  name={Platform.OS === "ios" ? "ios-add" : "menu-outline"}
  color="white"
  size={55}/>
</TouchableOpacity>
  <View style={{alignItems:'center',marginTop:35}}>
    <Text style={styles.textTitle}>
      {/* {sure} Alanı Rezerve Edildi */}
    </Text>
  </View>
  
  <View style={styles.timer}>
      {/* <CountDown
        size={30}
        // until={60}
      //   onFinish={() => 
          
      //     axios.get(`https://ieeevale.com/api/current_user`,{
