        timeLabels={{h:'Saat',m:'Dakika', s: 'Saniye'}}
        showSeparator
      /> */}
      <CountDownTimer
          ref={refTimer}
          timestamp={kalanSure}
          timerCallback={timerCallbackFunc}
          containerStyle={{
            marginBottom:15,
            height: 65,
            width: 150,
            justifyContent: 'center',
            alignItems: 'center',
            borderRadius: 15,
            backgroundColor: '#ffc107',
          }}
          textStyle={{
            fontSize: 25,
            color: 'black',
            fontWeight: '700',
            letterSpacing: 0.25,
          }}
        />
      
</View>
<View style={{alignItems:'center',marginTop:10}}>
