            source={require('../assets/trip1.png')}
            resizeMode="center"
            style={styles.image2}
          />
         
         

       
         

          <View style={{marginBottom:200}}>
          
          <TouchableOpacity style={{marginBottom:0}} onPress={() => this.props.navigation.navigate('homepage')}>
           
          <Icon  name={Platform.OS === "ios" ? "ios-add" : "caret-forward-outline"}
  color="black"
  size={105}/>
 </TouchableOpacity>
 
