import React, {useState,useEffect,Component} from 'react';
import { 
  View,
  Text,
  TextInput,
  StyleSheet,
  ScrollView,
  StatusBar,
  SafeAreaView,
  Touchable,
  Image,
  TouchableOpacity,
  Shape,FlatList,
  ImageBackground,
  Modal,
  Linking,
  Pressable
 } from 'react-native';
 import axios from "axios"
 import DrawerActions from "react-navigation"
 import Icon from "react-native-vector-icons/Ionicons"
 import LinearGradient from 'react-native-linear-gradient';
 import getDirections from 'react-native-google-maps-directions'
 import openMap from 'react-native-open-maps';
 import createMapLink from 'react-native-open-maps';
 export default class iücdoluluk extends Component{
 

  constructor(props)
  {

    super(props)
    this.state={
 doluluk:0,
 bosyer:0,
 modalVisible3:false,
 
 carPark2:{},
 show:false
    }
}
functionTwo(){
 console.log('navbar')
} 
_goToYosemite() {
  const {longitude2,latitude2} = this.props.route.params
   openMap({ latitude:latitude2, longitude:longitude2});
  // openMap({ latitude:41.005600, longitude:28.976857});
  createMapLink({ provider: 'google', end: 'Sultanahmet Camii' })
}
setModalVisible3 = (visible) => {
  this.setState({ modalVisible3: visible });
}



  componentDidMount() {
   
  }
  render(){
    
 const {carPark2,modalVisible3}=this.state

 const DATA = [
  {
    
    photo: 'https://www.carwiz.rent/data/public/2019-07/blue_mosque_15db837b846c60.jpg',
  },
  {
    
    photo: 'https://theistanbulinsider.com/wp-content/uploads/2020/03/blue-mosque-aerial.jpg',
  },
  {
   
    photo: 'https://lh3.googleusercontent.com/proxy/LUaKQlxnxwWZlqkcTho1z5sG1sCn3pNLgfKQX_z_tq98-Cb1vzLt5nIBvXwyHePXdfwGdyXUsjG4jwH14J9gFPGYeW5ysl2JdVffVyXlVAVZoL4CLlhLmtY',
  },
 
  {
    
    photo: 'https://besthqwallpapers.com/Uploads/12-2-2020/121329/thumb2-sultan-ahmed-mosque-blue-mosque-evening-sunset-istanbul-landmark.jpg',
  },
  {
    
    photo: 'https://previews.123rf.com/images/sarymsakov/sarymsakov1705/sarymsakov170500057/79201994-istanbul-turkey-1-april-2017-interior-of-blue-mosque-also-called-sultan-ahmed-mosque-or-sultan-ahmet.jpg',
  },
  {
    
    photo: 'https://img.theculturetrip.com/wp-content/uploads/2016/08/dscf4871.jpg',
   
  },
  {
    
    photo: 'https://www.google.com/search?q=sultan+ahmed+mosque&rlz=1C1SQJL_trTR938TR938&sxsrf=AOaemvLCN4BGOMNNQo55D_rawvh9alrErg:1633467506980&source=lnms&tbm=isch&sa=X&sqi=2&ved=2ahUKEwjFooXUlLTzAhWZfXAKHckHCaAQ_AUoAXoECAEQAw&biw=1920&bih=937&dpr=1'
    ,
    more:'More Photos..'
  },
];
const Item = ({ photo,more }) => (
  <View style={styles.item2}>

<TouchableOpacity  style={{alignSelf: 'center',justifyContent: 'center'}}onPress={()=>Linking.openURL(photo)}>
<Text style={{margin:'auto',fontSize:25,color:'grey',textAlign: 'center'}}>{more}</Text>
</TouchableOpacity>
    
    <TouchableOpacity  onPress={()=>Linking.openURL(photo)}>
     <Image
        style={{width:55, height:55,borderRadius:15}}
        source={{
          uri: photo
        }}
      />
      </TouchableOpacity>
  </View>
);
const renderItem = ({ item  }) => (
  <Item photo={item.photo} more={item.more}/>
);

return(
     
       <View style={{backgroundColor:'#1e1e01',width:'100%',height:'100%'}}>
<ImageBackground source={require('../../../../assets/Sultanahmet1.jpg')} resizeMode="cover" style={styles.image}>
     <View style={{alignItems:'center'}}> 
   <Text style={{color:'#40581d',fontSize:30,alignItems:'center',justifyContent:'center'}}>
     Sultanahmet Mosque
     </Text>
     </View>
<TouchableOpacity onPress={()=>this.props.navigation.navigate('mapIst')} style={{position:'absolute',right:0,top:0}}>
    <Icon  name={Platform.OS === "ios" ? "ios-add" : "arrow-back-circle-outline"}
  //name={(this.state.hidePassword)?"eye-off-outlane:eye-outlane"}  şifre görünürlüğü açıp kapatma
  color="#40581d"
  size={55}/>
</TouchableOpacity>
<TouchableOpacity onPress={()=> this.props.navigation.openDrawer(this.functionTwo()) } style={{position:'absolute',right:'85%',top:'0%'}}>
    <Icon  name={Platform.OS === "ios" ? "ios-add" : "menu-outline"}
  color="#40581d"
  size={55}/>
</TouchableOpacity>
<View style={{alignItems: 'center',marginTop:35}}>

<FlatList
        data={DATA}
        renderItem={renderItem}
        horizontal={true}
      />


  </View>


        
        {
        //GOOGLE MAPS DİRECTİON
        }
      
        <View style={{position: 'absolute', left: 0, right: 0, bottom: 550, flexDirection:'row-reverse',marginBottom:50,marginRight:5}}>
      <TouchableOpacity style={{alignItems: "center",
      
    backgroundColor: "#adf6d6",
    padding: 10, borderRadius:25}} onPress={()=>this.setModalVisible3(true)}>
      <Icon  name={Platform.OS === "ios" ? "ios-add" : "compass-outline"}
  color="black"
  size={45}>
        </Icon>
        
        </TouchableOpacity></View>
      
      
      
        {/* <View style={{position: 'absolute', left: 0, right: 0, bottom: 400, flexDirection:'row-reverse',marginBottom:150,marginRight:5}}>
      <TouchableOpacity style={{alignItems: "center",
      
    backgroundColor: "#adf6d6",
    padding: 10, borderRadius:55}} onPress={()=>Linking.openURL('https://www.google.com/search?q=sultan+ahmed+mosque&rlz=1C1SQJL_trTR938TR938&sxsrf=AOaemvJIqYWzjSq8w8-bfNO5dJcrDcdaIA:1633023811351&source=lnms&tbm=isch&sa=X&ved=2ahUKEwidh73hn6fzAhVpgf0HHXQRCVgQ_AUoAXoECAEQAw&biw=1126&bih=759&dpr=1')}>
      <Icon  name={Platform.OS === "ios" ? "ios-add" : "image-outline"}
  color="black"
