import React, {Component} from 'react';
import { 
  View,
  Text,
  StyleSheet,BackHandler,
  ScrollView,
  StatusBar,
  SafeAreaView,
  Touchable,
  Image,
  TouchableOpacity
 } from 'react-native';
import { createAppContainer } from "react-navigation";
import { createStackNavigator } from "react-navigation-stack";
import { createBottomTabNavigator } from "react-navigation-tabs";
import { createDrawerNavigator } from "react-navigation-drawer";
import Icon from "react-native-vector-icons/Ionicons"
import LinearGradient from 'react-native-linear-gradient';
export default class Drawer extends Component{
    constructor(){
      super();
  }
 
  
  render(){
  


     
  return (
    <LinearGradient colors={[ '#adf6d6','#adf6d6', '#adf6d6']} style={{width:'100%',height:'100%'}}>

<Image
            source={require('../assets/trip1.png')}
            resizeMode="center"
            style={styles.image2}
          />

        
          <TouchableOpacity style={{padding:15}} onPress={()=>this.props.navigation.closeDrawer()}>
          <Icon  name={Platform.OS === "ios" ? "ios-add" : "arrow-back-outline"}
  color="black"
  size={50}/>
  </TouchableOpacity>

  


  
  <TouchableOpacity style={{marginTop:25,padding:5}}onPress={()=>this.props.navigation.navigate('Login')}>
   
   
  <Icon  name={Platform.OS === "ios" ? "ios-add" : "home-outline"}
  color="black"
  size={40}>
<Text style={{

        fontSize:35, 
  margin: 15,
  padding: 10,
  width: 110,
  }}>
     Anasayfa
    </Text>
  </Icon>
   
  </TouchableOpacity>
  {/* <TouchableOpacity style={{marginTop:25,padding:5}}onPress={()=>this.props.navigation.navigate('iletisim')}>
   
   
  <Icon  name={Platform.OS === "ios" ? "ios-add" : "call-outline"}
  color="white"
  size={30}>
<Text style={{

        fontSize:35, 
  margin: 0,
  padding: 10,
  width: 110,
  }}>
     asdas
    </Text>
  </Icon>
   
  </TouchableOpacity> */}
<View style={{position: 'absolute', left: 0, right: 0, bottom: 0, flexDirection:'row-reverse',marginBottom:'35%',marginRight:'23%'}}>
  <TouchableOpacity style={{alignItems: "center",justifyContent: "center",width:150,borderRadius:105, height:75,backgroundColor:'red'}} 
  onPress={()=>
    BackHandler.exitApp()
  }>
   
   
   
 <Text style={{
 
         fontSize:35, 
   margin: 0,
   padding: 10,
   width: 110,
   color:'white'
   }}>
    ÇIKIŞ
     </Text>
   
    
   </TouchableOpacity>
   
   </View>
          </LinearGradient>
          
  )
  
  }
  }
  const styles = StyleSheet.create({
    
    image2: {
    
      resizeMode: 'contain',
      width: '75%',
      height: 165,
      marginVertical: 10,
   
  }});
